content.query:
AADRiskyUsers
//| where RiskDetail in ({RiskDetail}) or '*' in ({RiskDetail})
//| where RiskLevel in ({RiskLevel}) or '*' in ({RiskLevel})
| where RiskLevel == 'high'
| project TimeGenerated,UserDisplayName, RiskLevel, RiskDetail
| order by TimeGenerated

//let startTime = ago(90d);
//let endTime = now();
let noRisk = AADRiskyUsers
//| where TimeGenerated between (ago(14d) .. ago(8d))
| where RiskLevel == 'low' or RiskLevel == 'none'
| summarize by Id;
let highRisk = AADRiskyUsers
//| where TimeGenerated between (ago(7d).. now())
//| where UserDisplayName in (noRisk)
| where RiskLevel == 'high' or RiskLevel == 'medium'
| summarize by Id;
AADRiskyUsers
|where Id in (noRisk) and Id in (highRisk)
|summarize by RiskLastUpdatedDateTime, UserDisplayName, RiskLevel, RiskDetail
| order by RiskLastUpdatedDateTime
