let IPRegex = '[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}';
DeviceNetworkEvents
| where DeviceName in ({CompromDevice})
// Only list the succesfull connections
| where ActionType == "ConnectionSuccess"
// MDE Device Discovery filter
| where InitiatingProcessFileName <> "sensendr.exe"
// Add column that identifies RFC 1918 addresses
| extend IsPrivateAddress = ipv4_is_private(RemoteIP)
// Lateral movement is mostly identified by filtering on the private ip addresses and its connections.
// This query is build to detect private IP connections and not public ip connections.
| where IsPrivateAddress == 1
// Filter System related activities performed by local service
| where InitiatingProcessAccountName != "local service"
// Only list the last connection to each IP address.
| summarize arg_max(TimeGenerated, *), ConnectedPorts = make_set(RemotePort) by RemoteIP
| project
     RemoteIP,
     ConnectedPorts,
     RemoteUrl,
     InitiatingProcessAccountSid,
     InitiatingProcessAccountUpn,
     CompromisedDevice = DeviceName
// Join the network info of the remote IP address, to find the devicename that belongs to that IP
| join kind=inner (DeviceNetworkInfo
//     | where TimeGenerated > ago(SearchWindow)
     | extend IPAddres = extract(IPRegex, 0, tostring(IPAddresses))
     // See RFC 1918, these are not DHCP or Static addresses. Do leave them if you do NOT have DHCP running.
     | where not(ipv4_is_in_range(IPAddres, "169.254.0.0/16"))
     | distinct IPAddres, DeviceName)
     on $left.RemoteIP == $right.IPAddres
// Only list rows where the compromised device does not equal the RemoteDevice
| where not(CompromisedDevice  == DeviceName)
| project RemoteIP, RemoteDeviceName = DeviceName, ConnectedPorts, RemoteUrl, InitiatingProcessAccountSid, InitiatingProcessAccountUpn, CompromisedDevice
// Known false positives:
// Benign Edge Traffic, which did not initate a connection to an actual remote device.
//| where not(tostring(ConnectedPorts) == "[8009,8008]" or tostring(ConnectedPorts) == "[8008,8009]" or tostring(ConnectedPorts) == "[8009]" or tostring(ConnectedPorts) == "[8009]")

// Collect all ASR triggers from the compromised device
let ASREvents = DeviceEvents
     | where DeviceName in ({CompromDevice})
     | where ActionType startswith "ASR"
     | project TimeGenerated,DeviceName,ActionType, FileName, FolderPath, ProcessCommandLine, InitiatingProcessCommandLine,InitiatingProcessFolderPath, InitiatingProcessAccountDomain,InitiatingProcessAccountName,InitiatingProcessCreationTime,InitiatingProcessParentCreationTime, AccountDomain, AccountName;
// Collect all SmartScreen events from the compromised device
let SmartScreenEvents = DeviceEvents
     | where DeviceName in ({CompromDevice})
     | where ActionType in ('SmartScreenAppWarning', 'SmartScreenUrlWarning')
     | extend SmartScreenTrigger = iff(ActionType == "SmartScreenUrlWarning", RemoteUrl, FileName), ReasonForTrigger = parse_json(AdditionalFields).Experience
     | project TimeGenerated, DeviceName, ActionType, SmartScreenTrigger, ReasonForTrigger, InitiatingProcessCommandLine,InitiatingProcessFolderPath, InitiatingProcessAccountDomain, InitiatingProcessAccountName, InitiatingProcessParentFileName,InitiatingProcessCreationTime,InitiatingProcessParentCreationTime;
// List all AV detections from the compromised device
let AntivirusDetections = DeviceEvents
     | where DeviceName in ({CompromDevice})
     | where ActionType == "AntivirusDetection"
     | extend ThreatName = tostring(parse_json(AdditionalFields).ThreatName)
     | extend WasExecutingWhileDetected = tostring(AdditionalFields.WasExecutingWhileDetected)
     | extend WasRemediated = tostring(AdditionalFields.WasRemediated)
     | project TimeGenerated, DeviceName, ActionType, ThreatName, FileName, FolderPath, SHA1, InitiatingProcessAccountSid,WasExecutingWhileDetected, WasRemediated ;
// List all tampering actions from a compromised device
let TamperingAttempts = DeviceEvents
     | where DeviceName in ({CompromDevice})
     | where ActionType == "TamperingAttempt"
     | extend TamperingAction = tostring(parse_json(AdditionalFields).TamperingAction), Status = tostring(parse_json(AdditionalFields).Status), Target = tostring(parse_json(AdditionalFields).Target)
     | project TimeGenerated, DeviceName, ActionType, TamperingAction, Status, Target, InitiatingProcessCommandLine;
// List all exploit guard events
let ExploitGuardEvents = DeviceEvents
     | where DeviceName in ({CompromDevice})
     | where ActionType startswith "ExploitGuard"
     | project TimeGenerated, DeviceName, ActionType, InitiatingProcessCommandLine, InitiatingProcessFileName, InitiatingProcessFolderPath, InitiatingProcessParentFileName,InitiatingProcessVersionInfoFileDescription, FileName, FolderPath, RemoteUrl;
// List all amsi events
let AMSIEvents = DeviceEvents
     | where DeviceName in ({CompromDevice})
     | where ActionType contains "Amsi"
     | extend Description = tostring(parse_json(AdditionalFields).Description)
     | project TimeGenerated, DeviceName, ActionType,InitiatingProcessCommandLine,InitiatingProcessFileName,InitiatingProcessFolderPath,InitiatingProcessAccountDomain,InitiatingProcessAccountName,InitiatingProcessParentFileName, Description, FolderPath,InitiatingProcessCreationTime,InitiatingProcessParentCreationTime ;
// Combine all results into one output
(union isfuzzy=true
     (ASREvents),
     (SmartScreenEvents),
     (AntivirusDetections),
     (TamperingAttempts),
     (ExploitGuardEvents),
     (AMSIEvents)
     | sort by TimeGenerated
)

